mapper.update:
UPDATE "TB_BOARD"
SET
	BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{boardNo}
AND BOARD_CODE = #{boardCode}
AND MEMBER_NO = #{memberNo}

UPDATE "TB_BOARD"
SET
	BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{boardNo}
AND BOARD_CODE = #{boardCode}

UPDATE "TB_BOARD"
SET
	BOARD_TITLE = #{boardTitle},
	BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}
AND BOARD_CODE = #{boardCode}
AND MEMBER_NO = #{memberNo}
